import React, { useState, useEffect } from "react";
import "./Header.css";
import menuIcon from '../../assets/images/burger-menu-right-svgrepo-com.svg';

function Header() {
  const [isMenuOpen, setMenuOpen] = useState(false);
  const [isProjectsVisible, setProjectsVisible] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
  };

  const handleScroll = () => {
    const projectsSection = document.getElementById("projets");
    const rect = projectsSection.getBoundingClientRect();
    const isVisible = rect.top <= 0 && rect.bottom >= 0;
    setProjectsVisible(isVisible);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const menuButtonClass = isMenuOpen ? 'menu-button rotate' : 'menu-button';
  const menuClass = isMenuOpen ? 'open' : '';
  const menuIconClass = isProjectsVisible ? 'img-menu projects-active' : 'img-menu';

  return (
    <main>
      <header className={isProjectsVisible ? 'projects-active' : ''}>
        <ul className="header-left">
          <li>
            <a href="#a-propos">Bastien Cochet</a>
          </li>
          <li className="li-job">
            <a href="#a-propos">Dev Web Junior</a>
          </li>
        </ul>
        <nav className="header-right">
          <div className={menuButtonClass} onClick={toggleMenu}>
          <img
  className={`img-menu ${isProjectsVisible ? 'projects-active' : ''}`}
  src={menuIcon}
  alt="Menu Icon"
  style={{ width: '2rem', height: '2rem', fill: isProjectsVisible ? '#000' : '#f3f3f3' }}
/>


          </div>
          <ul className={menuClass}>
            <li>
              <a href="#a-propos">À propos</a>
            </li>
            <li>
              <a href="#projets">Projets</a>
            </li>
            <li>
              <a href="#skills">Compétences</a>
            </li>
            <li>
              <a href="#contact">Contact</a>
            </li>
          </ul>
        </nav>
      </header>
    </main>
  );
}

export default Header;
